/* strcast V1.0.1 by Lenglet Anthony // Usage: "Hello ..name..!".strcast({name: 'world'}) */
String.prototype.strcast=function(a){var b=this,c=b.match(/\.\.[a-zA-Z0-9]+\.\./g),d;for(var i=0;i<c.length;i++){var b=b.replace(c[i],function(){d=a[c[i].substring(2,c[i].length-2)];return d!=undefined?d:c[i];});}return b;};
